    $('.removeCartItem').click(function(){
        var rowid = $(this).attr('data-rowid');
        $.ajax({
            url: '/ajax/cartRemove',
            type: 'POST',
            data: {
                rowid: rowid
            },
            error: function() {
                alert('ERROR!');
            },
            success: function(res) {
                if(res < 1){
                    location.reload();
                }else{
                    $('tr[data-rowid='+ rowid +']').remove();
                    $('.total').html(res);
                }
            }
        });
    });

    $('.editQty').change(function(){
        var rowid = $(this).attr('data-rowid');
        var qty = $(this).val();
        if(qty > 0){
            $.ajax({
                url: '/ajax/cartQty',
                type: 'POST',
                dataType: 'json',
                data: {
                    rowid: rowid,
                    qty: qty
                },
                error: function() {
                    noty_show('alert', 'Кол-во не изменено');
                },
                success: function(res) {
                    $('.total').html(res.total);
                    $('tr[data-rowid='+ rowid +']').find('.subtotal span').html(res.subtotal);
                    noty_show('success', 'Кол-во изменено');
                }
            });
        }
    });

    /* http://ned.im/noty/#/about */
    function noty_show(type, message, container){
        if(container){
            if(type === 'alert'){
                container.noty({
                    theme: 'relax',
                    layout: 'center',
                    text: message,
                    type: 'alert',
                    modal: true
                })
            }else{
                container.noty({
                    theme: 'relax',
                    layout: 'center',
                    text: message,
                    type: type,
                    timeout: 1500
                })
            }
        }else{
            if(type === 'alert'){
                noty({
                    theme: 'relax',
                    layout: 'center',
                    text: message,
                    type: 'alert',
                    modal: true
                })
            }else{
                noty({
                    theme: 'relax',
                    layout: 'center',
                    text: message,
                    type: type,
                    timeout: 1500
                })
            }
        }
    }
